import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import './Expenses.css';
import ExpensesFilter from "./ExpensesFilter";

const Expenses = (props) => {
    const [filteredYear, setFilteredYear] = useState('2020');

    const changeFilterHandler = selectedYear => {
        setFilteredYear(selectedYear);


    }
    const filteredExpenseItem = props.items.filter(expense => expense.date.getFullYear().toString() == filteredYear);
    console.log(filteredExpenseItem);

    return (
        <div>
            <Card className="expenses">
                <ExpensesFilter
                    selected={filteredYear}
                    onChangeFilter={changeFilterHandler}
                />
                {filteredExpenseItem.lenght === 0 ? (<p>No expenses found.</p>) :
                    (filteredExpenseItem.map((expense) => (
                        <ExpenseItem
                            key={expense.id}
                            title={expense.title}
                            amount={expense.amount}
                            date={expense.date}
                        />))
                    )}

            </Card>
        </div>
    );
}

export default Expenses;
